import React, { useEffect, useCallback, useMemo, useState, MouseEvent } from 'react';
import styled, { keyframes, DefaultTheme } from 'styled-components';
import { darken } from 'polished';
import Drawer from '@material-ui/core/Drawer';
import LinearProgress from '@material-ui/core/LinearProgress';
import Grid from '@material-ui/core/Grid';
import { Box, BoxBody, BoxHeader, BoxTitle } from 'src/components/Box';
import Container from 'src/components/Container';
import Page from 'src/components/Page';
import HowItWorks from './HowItWorks';
import { useConfiguration } from 'src/contexts/ConfigProvider/ConfigProvider';
import useDiamondHand from 'src/hooks/useDiamondHand';

import Input from 'src/components/Input';
import { useWeb3React } from '@web3-react/core';
import useTryConnect from 'src/hooks/useTryConnect';
import NumberDisplay from 'src/components/Number';
import { useTokenBalance } from 'src/contexts/AccountBalanceProvider/AccountBalanceProvider';
import { BigNumber } from 'ethers';
import Spacer from 'src/components/Spacer';
import { ButtonHarvest } from './ButtonHarvest';
import { formatSecs } from 'src/utils/formatTime';
import useApprove, { ApprovalState } from 'src/hooks/useApprove';
import Badge from '@material-ui/core/Badge';
import Item from './Item';
import Cart from './Cart';
import { OpenSeaPort, Network } from 'opensea-js';
export type Data = {
  name: string;
  img: string;
  id: number;
  amount: number;
  price: number;
};

export type CartItemType = {
  name: string;
  img: string;
  id: number;
  amount: number;
};

enum ButtonStatus {
  notConnected = 1,
  insufficient = 2,
  requireApproval = 3,
  approvalPending = 4,
  paused = 15,
  ready = 20,
  notEnough = 21,
}

const Farms: React.FC = () => {
  const dh = useDiamondHand();
  const config = useConfiguration();
  const { tryConnect } = useTryConnect();
  const { account } = useWeb3React();
  const nftaddress = config.addresses.WhiteListedNFT;

  const [mouse, setmouse] = useState<boolean>(false);
  const [mouse1, setmouse1] = useState<boolean>(false);

  const [cartOpen, setCartOpen] = useState(false);
  const [error, seterror] = useState(false);
  const { deployments } = config;
  const [data, setData] = React.useState<Data[]>([]);
  const [cartItems, setCartItems] = useState([] as Data[]);

  const ButtonConnect = useMemo(() => {
    return (
      <StakeButtonStyled color="primary" onClick={tryConnect}>
        Connect
      </StakeButtonStyled>
    );
  }, [tryConnect]);

  const onClickOpen = useCallback(async () => {
    setCartOpen(!cartOpen);
  }, [cartOpen]);

  const onClickGetData = async () => {
    await fetch(
      `https://api.opensea.io/api/v1/assets?owner=0xEf768Ef781637a7995a2B57fF77825E8aBcF682D&asset_contract_address=0xb47e3cd837dDF8e4c57F05d70Ab865de6e193BBB&order_direction=desc&offset=0&limit=20`,
    )
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(result);
          for (let i = 0; i < result?.length; i++) {
            data.push({
              name: result.asset_events[i]?.asset.name,
              id: result.asset_events[i]?.asset.token_id,
              img: result.asset_events[i]?.asset.image_url,
              amount: 0,
              price: 5000,
            });
          }
        },
        (error) => seterror(!error),
      );

    console.log(data[3]?.img);
  };
  window.onload = function () {
    onClickGetData();
  };

  const handleAddToCart = (clickedItem: Data) => {
    setCartItems((prev) => {
      // 1. Is the item already added in the cart?
      const isItemInCart = prev.find((item) => item.id === clickedItem.id);

      if (isItemInCart) {
        return prev.map((item) =>
          item.id === clickedItem.id ? { ...item, amount: item.amount + 1 } : item,
        );
      }
      // First time the item is added
      return [...prev, { ...clickedItem, amount: 1 }];
    });
  };

  const handleRemoveFromCart = (id: number) => {
    setCartItems((prev) =>
      prev.reduce((ack, item) => {
        if (item.id === id) {
          if (item.amount === 1) return ack;
          return [...ack, { ...item, amount: item.amount - 1 }];
        } else {
          return [...ack, item];
        }
      }, [] as Data[]),
    );
  };
  const getTotalItems = (items: Data[]) =>
    items.reduce((ack: number, item) => ack + item.amount, 0);
  return (
    <Page>
      <Container size="md">
        <Drawer anchor="right" open={cartOpen} onClose={() => setCartOpen(false)}>
          <Wrapper>
            <Cart
              cartItems={cartItems}
              addToCart={handleAddToCart}
              removeFromCart={handleRemoveFromCart}
            />
          </Wrapper>
        </Drawer>
        <StakeButtonStyled onClick={onClickOpen}>Show Cart</StakeButtonStyled>
        <Grid container spacing={4}>
          {data?.map((item) => (
            <Grid item key={item.id} xs={12} sm={4}>
              <Item item={item} handleAddToCart={handleAddToCart} />
            </Grid>
          ))}
        </Grid>
