import React from 'react';
import { Route, BrowserRouter as Router, Switch, Redirect } from 'react-router-dom';
import styled, { ThemeProvider } from 'styled-components';
import Popups from './components/Popups';
import Updaters from './state/Updaters';
import theme from './theme';
import store from './state';
import { Provider } from 'react-redux';
import ModalsProvider from './contexts/Modals';
import CuffiesBankProvider from './contexts/DiamondHandProvider';
import { ConfigProvider } from './contexts/ConfigProvider/ConfigProvider';
import { DynamicWalletProvider } from './contexts/DynamicWalletProvider/DynamicWalletProvider';
import Header from './components/Header';
import LoadingModal from './components/Loading/LoadingModal';
import { GlobalStyle } from './GlobalStyle';
import MainWrapper from './MainWrapper';
import loadable from '@loadable/component';
import { withPreload } from './hooks/usePreload';
import DragonBall from './views/DragonBall';
import Farms from './views/Farms';
import BuyLottery from './views/DragonBall/components/BuyLottery';
import TicketDetail from './views/TicketDetail';

import Monitor from './views/Monitor';

const App: React.FC = () => {
  return (
    <Providers>
      <StyledSite>
        <Router>
          <MainWrapper>
            <StyledHeaderContainer>
              <Header />
            </StyledHeaderContainer>
            <Switch>
              <Route path="/" exact>
                <Farms />
              </Route>

              <Route path="/farms">
                <Farms />
              </Route>
              <Redirect to="/" />
            </Switch>
          </MainWrapper>
        </Router>
      </StyledSite>
    </Providers>
  );
};

const StyledSite = styled.div``;

const StyledHeaderContainer = styled.div`
  width: 100%;
  /* padding: 30px 44px; */
  margin-bottom: 20px;
  padding: 20px 8px 0 8px;

  @media (min-width: ${({ theme }) => theme.breakpoints.md}) {
    padding: 20px 24px 0 24px;
  }

  @media (min-width: ${({ theme }) => theme.breakpoints.lg}) {
    padding: 30px 44px;
  }
  @media (max-width: ${({ theme }) => theme.breakpoints.sm}) {
    padding: 20px 16px 0 16px;
  }
`;

const Providers: React.FC = ({ children }) => {
  return (
    <ThemeProvider theme={theme}>
      <GlobalStyle />
      <ConfigProvider>
        <DynamicWalletProvider>
          <Provider store={store}>
            <Updaters />
            <CuffiesBankProvider>
              <ModalsProvider>
                <>
                  <LoadingModal />
                  <Popups />
                  {children}
                </>
              </ModalsProvider>
            </CuffiesBankProvider>
          </Provider>
        </DynamicWalletProvider>
      </ConfigProvider>
    </ThemeProvider>
  );
};

export default withPreload(App, 10e3);
