import CartItem from './CartItem';
import styled from 'styled-components';
import { Data } from './Farms';
import { ModalStake } from './ModalStake';
import useModal from 'src/hooks/useModal';
import Drawer from '@material-ui/core/Drawer';
import React, { useCallback, useState } from 'react';
import Item from './Item';
type Props = {
  cartItems: Data[];
  addToCart: (clickedItem: Data) => void;
  removeFromCart: (id: number) => void;
};

const Cart: React.FC<Props> = ({ cartItems, addToCart, removeFromCart }) => {
  const [cartOpen, setCartOpen] = useState(false);

  const calculateTotal = (items: Data[]) =>
    items.reduce((ack: number, item) => ack + item.amount * item.price, 0);
  const onClickOpen = useCallback(async () => {
    setCartOpen(!cartOpen);
  }, [cartOpen]);
  const [term, setTerm] = useState('');
  const submitForm = (event: React.FormEvent<HTMLFormElement>) => {
    // Preventing the page from reloading
    event.preventDefault();

    // Do something
    alert(term);
  };
  const [showStake] = useModal(<ModalStake id={'1'} />);
  return (
    <Wrapper>
      <h2>Your Orders:</h2>
      {cartItems.length === 0 ? <p>No items in cart.</p> : null}
      {cartItems.map((item) => (
        <CartItem
          key={item.id}
          item={item}
          addToCart={addToCart}
          removeFromCart={removeFromCart}
        />
      ))}
      <h2>Total: ${calculateTotal(cartItems).toFixed(2)}</h2>
      <Drawer anchor="right" open={cartOpen} onClose={() => setCartOpen(false)}>
        <Wrapper>
          <form onSubmit={submitForm}>
            <input
              value={term}
              onChange={(e) => setTerm(e.target.value)}
              type="text"
              placeholder="Enter a term"
              className="input"
            />
            <input
              value={term}
              onChange={(e) => setTerm(e.target.value)}
              type="text"
              placeholder="Enter a term"
              className="input"
            />
            <button type="submit" className="btn">
              Submit
            </button>
          </form>
        </Wrapper>
      </Drawer>
      <ButtonStyled onClick={onClickOpen}>Show Cart</ButtonStyled>
    </Wrapper>
  );
};

export default Cart;

const Wrapper = styled.aside`
  font-family: Arial, Helvetica, sans-serif;
  width: 500px;
  padding: 20px;
`;
const ButtonStyled = styled.button`
  font-size: 1rem;
  font-weight: 600;
  color: white;
  text-align: center;
  width: 150px;
  height: 50px;
  border-radius: 5px;
  margin-left: auto;
  margin-right: auto;
  z-index: 6;
  position: relative;
  overflow: hidden;
`;
