import React, { useEffect, useCallback, useMemo, useState, MouseEvent } from 'react';
import styled, { keyframes, DefaultTheme } from 'styled-components';
import { darken } from 'polished';
import Drawer from '@material-ui/core/Drawer';
import LinearProgress from '@material-ui/core/LinearProgress';
import Grid from '@material-ui/core/Grid';
import { Box, BoxBody, BoxHeader, BoxTitle } from 'src/components/Box';
import Container from 'src/components/Container';
import Page from 'src/components/Page';
import HowItWorks from './HowItWorks';
import { useConfiguration } from 'src/contexts/ConfigProvider/ConfigProvider';
import useDiamondHand from 'src/hooks/useDiamondHand';
import { Form } from './Form1';
import { Field } from './Field';

import Input from 'src/components/Input';
import { useWeb3React } from '@web3-react/core';
import useTryConnect from 'src/hooks/useTryConnect';
import NumberDisplay from 'src/components/Number';
import { useTokenBalance } from 'src/contexts/AccountBalanceProvider/AccountBalanceProvider';
import { BigNumber } from 'ethers';
import Spacer from 'src/components/Spacer';
import { ButtonHarvest } from './ButtonHarvest';
import { formatSecs } from 'src/utils/formatTime';
import useApprove, { ApprovalState } from 'src/hooks/useApprove';
import Badge from '@material-ui/core/Badge';
import Item from './Item';
import { Data } from './Farms';
import Cart from './Cart';
import { initializeApp } from 'firebase/app';
import { getAnalytics } from 'firebase/analytics';
import { getDatabase, ref, set, child, get } from 'firebase/database';
import axios from 'axios';
import pinataSDK from '@pinata/sdk';
import json from './test.json';
type Props = {
  Nftid: number;
  total: number;
};

const Upload: React.FC = (total) => {
  const [name, setName] = useState<string>();
  const [address, setAddress] = useState<string>();
  const [email, setEmail] = useState<string>();
  const [orderId, setorderId] = useState<string>();
  const [myHeaders, setMyheaders] = useState<Headers>();
  const submitForm = (event: React.FormEvent<HTMLFormElement>) => {
    // Preventing the page from reloading
    event.preventDefault();
    setPayment();
    // Do something
    alert(name);
  };

  const data =
    '{\n    "symbol": "ETH",\n    "fromAmount": 90,\n    "referenceId": "test 1",\n    "userIp": "192.168.1.4.",\n    "fromUserSymbol": true\n}';

  const config = {
    method: 'post',
    url: 'https://api.tomapay.com/rest/v1/generate/payment',
    headers: {
      Authorization: 'Bearer Ea4XvxLTMqJN6JHc4',
    },
    data: data,
  };
  function setPayment() {
    axios
      .post(config.url, data, {
        headers: {
          'Access-Control-Allow-Origin': '*',
          Authorization: 'Bearer xADhx2yPhzFmYeXjc',
        },
      })
      .then(function (response) {
        console.log(response);
        //handle response here
      })
      .catch(function (error) {
        //handle error here
      });
  }

  const pinata = pinataSDK(
    '1dc5a291e94cb3f5059a',
    '4ec9927409d906d50533daa94de915171f66f8bd5d5dc2ce6e89f76c8c303817',
  );

  const metadataFilter = {
    name: 'meta',
    keyvalues: {},
  };
  const filters = {
    status: 'pinned',
    pageLimit: 10,
    pageOffset: 0,
    metadata: metadataFilter,
  };
  pinata
    .pinList(filters)
    .then((result) => {
      //handle results here
      console.log(result);
    })
    .catch((err) => {
      //handle error here
      console.log(err);
    });
  const metadata = JSON.stringify({
    name: name,
    email: email,
    address: address,
  });
  function newhash(hashToPin: string) {
    const url = `https://api.pinata.cloud/pinning/pinByHash`;
    const body = {
      hashToPin: hashToPin,
      hostNodes: [
        '/ip4/hostNode1ExternalIP/tcp/4001/ipfs/hostNode1PeerId',
        '/ip4/hostNode2ExternalIP/tcp/4001/ipfs/hostNode2PeerId',
      ],
      pinataMetadata: {
        name: 'kek',
        keyvalues: {
          customKey: 'customValue',
          customKey2: 'customValue2',
        },
      },
    };
    return axios
      .post(url, body, {
        headers: {
          pinata_api_key: '1dc5a291e94cb3f5059a',
          pinata_secret_api_key:
            '4ec9927409d906d50533daa94de915171f66f8bd5d5dc2ce6e89f76c8c303817',
        },
      })
      .then(function (response) {
        console.log(response);
        //handle response here
      })
      .catch(function (error) {
        //handle error here
      });
  }
  function MetaName(hash: string) {
    const url = `https://api.pinata.cloud/pinning/hashMetadata`;
    const body = {
      ipfsPinHash: hash,
      name: orderId,
      keyvalues: {
        newKey: 'newValue',
        existingKey: 'newValue',
      },
    };
    return axios
      .put(url, body, {
        headers: {
          pinata_api_key: '1dc5a291e94cb3f5059a',
          pinata_secret_api_key:
            '4ec9927409d906d50533daa94de915171f66f8bd5d5dc2ce6e89f76c8c303817',
        },
      })
      .then(function (response) {
        console.log('newname generated');
      })
      .catch(function (error) {
        //handle error here
      });
  }

  function writeUserData(JSONBody: string) {
    const url = `https://api.pinata.cloud/pinning/pinJSONToIPFS`;
    return axios
      .post(url, JSONBody, {
        headers: {
          pinata_api_key: '1dc5a291e94cb3f5059a',
          pinata_secret_api_key:
            '4ec9927409d906d50533daa94de915171f66f8bd5d5dc2ce6e89f76c8c303817',
        },
      })
      .then(function (response) {
        console.log(response?.data.IpfsHash);
        newhash(response?.data.IpfsHash);
      })
      .catch(function (error) {
        //handle error here
      });
  }

  return (
    <form onSubmit={submitForm}>
      <ul>
        Name:
        <input
          value={name}
          onChange={(e) => setName(e.target.value)}
          type="text"
          placeholder="Enter name"
          className="input"
        />
      </ul>
      <ul>
        Email:
        <input
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          type="text"
          placeholder="Enter address"
          className="input"
        />
      </ul>
      <ul>
        Address:
        <input
          value={address}
          onChange={(e) => setAddress(e.target.value)}
          type="text"
          placeholder="Enter address"
          className="input"
        />
